import React, {useEffect, useReducer, useState,useRef} from 'react';
import './Mapcopy.css';
import { MapContainer } from 'react-leaflet/MapContainer'
import { TileLayer } from 'react-leaflet/TileLayer'
import 'leaflet/dist/leaflet.css';
import LineRender from './LineRender'

const API = 'http://localhost:8000/api/lines/'
const axios = require('axios');

function Map1() {
  
  const [lineData,getlineData] = useState('')
  const [threshold,setThreshold] = useState('')
  const getalllineData = () => {
    axios.get(API)
    .then((response) => {
      const allLines = response.data
      getlineData(allLines)
    })
  }

  useEffect(() => {
    getalllineData();
  }, []);

  const handleChange = event => {
    setThreshold(event.target.value)
  }
  const handleClick = event => {
    
  }
  return ( 
    <div className="background" > 

     
      <header className = "header1">
        <div className = "logout">
            Log Out
        </div>
      </header>

    
    <div className="map">
    <div>
      <form>
        <label>
          Update Threshold:
            <input type="text" name="Threshold" onChange={handleChange} ></input>
        </label>
        <input type="submit" value="Update" onClick={handleClick}></input> 
        <h3>Current Threshold: {threshold}</h3>
        
      </form>
    </div>

    <MapContainer  className = 'map' center={[38.5, -121.7]} zoom={9} scrollWheelZoom={true}>

      <TileLayer
      attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />

      <LineRender lines={lineData} thres={threshold}></LineRender>
    </MapContainer>
    </div>
    
    
    </div>
  );
}

export default Map1;
