import React, {useEffect, useReducer, useState,useRef} from 'react';
import './Mapcopy.css';
import { MapContainer } from 'react-leaflet/MapContainer'
import { TileLayer } from 'react-leaflet/TileLayer'
import { useMap } from 'react-leaflet/hooks'
import {
    Marker, Popup, Polyline, Polygon
} from 'react-leaflet'
import 'leaflet/dist/leaflet.css';
import { map } from 'leaflet';
import line1 from './line1.json'
import line2 from './line2.json'
import { GeoJSON  } from 'react-leaflet';
import {Map} from './Map'
import L from 'leaflet'

const API = 'http://localhost:8000/api/lines/'
const axios = require('axios');

function Map1() {
  
  const [lineData,getlineData] = useState('')
  const getalllineData = () => {
    axios.get(API)
    .then((response) => {
      const allLines = response.data
      getlineData(allLines)
    })
  }

  useEffect(() => {
    getalllineData();
}, []);

  /* (async () => {
    let data
    function status() {
      return axios.get(API).then((resp) => {
        return resp.data;
      });
    }
    data = await status();
    this.setState({data})
    console.log(data);
  })();  */   

  console.log(lineData)
  const produce = (data,i) => {
    useEffect(() => {
      getalllineData();
  }, []);
    var geo = data[i].geometry
    var riskValue = data[i].wear * data[i].weather * data[i].vegetation
    return <GeoJSON data={geo}></GeoJSON>
  }
  

  

  const l1 = line1
  const l2 = line2
  const risk = 0
  var r = ''


  const newLine = (data,rate) => {
    if(rate > risk)
      r = 'red'
    else
      r = 'green'
    return <GeoJSON data={data} pathOptions= {{ color: r}}></GeoJSON>
  }


  return ( 
    <div > 

     
      <header className = "header1">
        <div className = "logout">
            Log Out
        </div>
      </header>

      
      <MapContainer  className = 'test1' center={[38.5, -121.7]} zoom={9} scrollWheelZoom={false}>
      <TileLayer
    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
    {produce(lineData,1)}
    </MapContainer>
      
      
      

    </div>
  );
}

export default Map1;
